package com.example.arms.appimport android.app.Activityimport android.content.ContentResolverimport android.content.Contextimport android.content.res.Resourcesimport android.net.Uriimport android.os.Buildimport android.provider.MediaStoreimport androidx.fragment.app.DialogFragmentimport androidx.fragment.app.Fragmentimport androidx.appcompat.app.AppCompatActivityimport android.view.Viewimport android.view.View.GONEimport android.view.View.VISIBLEimport android.view.ViewGroupimport android.view.inputmethod.InputMethodManagerimport android.widget.ImageViewimport com.jess.arms.http.imageloader.ImageConfigimport com.jess.arms.integration.AppManagerimport com.jess.arms.utils.ArmsUtilsimport com.jess.arms.utils.DeviceUtilsval Float.px: Float get() = (this * Resources.getSystem().displayMetrics.density)val Int.px: Int get() = ((this * Resources.getSystem().displayMetrics.density).toInt())/** * 点击事件扩展方法 */fun View.onClick(method: (() -> Unit)?): View {    setOnClickListener { method?.invoke() }    return this}/** * 点击事件扩展方法 */fun View.onClick(listener: View.OnClickListener): View {    setOnClickListener(listener)    return this}/** * 设置View的可见 */fun View.visible(isVisible: Boolean): View {    visibility = if (isVisible) VISIBLE else GONE    return this}fun AppCompatActivity.showDialog(dialog: androidx.fragment.app.DialogFragment) {    dialog.show(supportFragmentManager, "TAG")}fun androidx.fragment.app.Fragment.showDialog(dialog: androidx.fragment.app.DialogFragment) {    dialog.show(fragmentManager, "TAG")}fun dismissDialog(dialog: androidx.fragment.app.DialogFragment) {    dialog.dismiss()}/** * */fun <T : ImageConfig> ImageView.loadImage(config: T) {    ArmsUtils.obtainAppComponentFromContext(this.context)        .imageLoader()        .loadImage(this.context, config)}fun ImageView.loadImage(url: String) {    loadImage(url, 0)}fun ImageView.loadImage(url: String, placeholder: Int) {    ArmsUtils.obtainAppComponentFromContext(this.context)        .imageLoader().loadImage(this.context, ArmsImageConfig.Builder()            .url(url)            .placeholder(placeholder)            .imageView(this)            .build())}fun Context.getTopActivity(): Activity {    return AppManager.getAppManager().topActivity!!}/** * 通过uri  获取文件的路径 */fun Uri.getRealFilePath(context: Context): String? {    val scheme = this.getScheme()    var data: String? = null    if (scheme == null)        data = this.getPath()    else if (ContentResolver.SCHEME_FILE == scheme) {        data = this.getPath()    } else if (ContentResolver.SCHEME_CONTENT == scheme) {        val cursor = context.contentResolver.query(this, arrayOf(MediaStore.Images.ImageColumns.DATA), null, null, null)        if (null != cursor) {            if (cursor.moveToFirst()) {                val index = cursor.getColumnIndex(MediaStore.Images.ImageColumns.DATA)                if (index > -1) {                    data = cursor.getString(index)                }            }            cursor.close()        }    }    return data}fun androidx.fragment.app.Fragment.hideKeyboard() {    activity?.hideKeyboard()}fun Activity.hideKeyboard() {    val imm =        getSystemService(android.content.Context.INPUT_METHOD_SERVICE) as InputMethodManager    val currentFocus = window.currentFocus    val windowToken = if (currentFocus != null) {        currentFocus.windowToken    } else {        window.decorView.windowToken    }    if (windowToken != null) {        imm.hideSoftInputFromWindow(windowToken, 0)    }}/** * 设置状态栏内边距 */fun Activity.setStatusBarPadding(){    if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.KITKAT) {        val view = window.decorView.findViewById<ViewGroup>(android.R.id.content).getChildAt(0)        val marginTop = DeviceUtils.getStatusBarHeight(view.context)        view.setPadding(view.paddingLeft, marginTop,            view.paddingRight, view.paddingBottom        )    }}